class color:
    '''coulours is a class witch permit to print a string or a variable in colour:
    grey
    red
    green
    yellow
    blue
    magenta
    cyan
    '''
    
    def __init__(self):
        self.surface =""
    
    def grey(self, message):
        print('\033[1;30m \033[1;m' + message)            
    def red(self, message): 
        print('\033[1;31m \033[1;m' + message)
    def green(self, message):
        print('\033[1;32m \033[1;m' + message)
    def yellow(self, message):
        print('\033[1;33m \033[1;m' + message)
    def blue(self, message):
        print('\033[1;34m \033[1;m' + message)                
    def magenta(self, message):
        print('\033[1;35m \033[1;m' + message)    
    def cyan(self, message):
        print('\033[1;36m \033[1;m' + message)                    

